package api.ultis;import io.restassured.http.ContentType;import io.restassured.path.json.JsonPath;import io.restassured.response.Response;import java.util.List;import java.util.Map;import static io.restassured.RestAssured.given;public class ApiProvinceInfor {    private String baseUri;    private List<Map<String, String>> duLieu;    private Map<String, List<Map<String, String>>> andressInfor;    public ApiProvinceInfor(String baseUri){        this.baseUri =baseUri;        getAndressInfor();    }    public String getDuLieuMa(String duLieustr) {        dulieu();        String ma = null;        for (Map<String, String> issueType : duLieu) {            if (issueType.get("ten").equalsIgnoreCase(duLieustr)) {                ma = issueType.get("ma");                break;            }        }        if (ma == null) {            throw new RuntimeException("ERR Not find the id for: " + duLieustr);        }        return ma;    }    private void dulieu() {        duLieu = andressInfor.get("dulieu");    }    private void getAndressInfor() {        Response response = given()                .contentType(ContentType.JSON)                .when()                .get(baseUri);        andressInfor =JsonPath.from(response.asString()).get();    }}