package api.ultis;import io.restassured.http.ContentType;import io.restassured.path.json.JsonPath;import io.restassured.response.Response;import java.util.List;import java.util.Map;import static io.restassured.RestAssured.given;public class ApiQuanHuyenInfor {    private String ma;    private String baseUri;    private List<Map<String, String>> duLieu;    private Map<String, List<Map<String, String>>> andressInfor;    public ApiQuanHuyenInfor(String baseURI, String maTinhThanh, String endpoint) {    }    public String getDuLieuMa(String duLieustr) {        if (andressInfor == null) {            throw new RuntimeException("Error: Address information is null.");        }        dulieu();        String ma = null;        for (Map<String, String> issueType : duLieu) {            if (issueType.get("ten").equalsIgnoreCase(duLieustr)) {                ma = issueType.get("ma");                break;            }        }        if (ma == null) {            throw new RuntimeException("ERR Not find the id for: " + duLieustr);        }        return ma;    }    private void dulieu() {        duLieu = andressInfor.get("dulieu");    }    private void getAndressInfor(String endpoint) {        Response response = given()                .contentType(ContentType.JSON)                .when()                .pathParam("ma", ma)                .queryParam("dt", 461)                .get(endpoint);        andressInfor = JsonPath.from(response.asString()).get();    }}