package api.ultis;import io.restassured.http.Header;public interface HeaderApi {    Header deafautLHeader = new Header("Content-type", "aapplication/x-www-form-urlencoded; charset=UTF-8");    static Header getTokenAuthorization(String token) {        if (token == null) {            throw new IllegalArgumentException("[ERR] token can't be null");        } else {            return new Header("Authorization", token);        }    }}